    name: "secgroup",
});
const port1 = new openstack.networking.PortSecGroupAssociate("port_1", {
    portId: systemPort.then(systemPort => systemPort.id),
    securityGroupIds: [secgroup.then(secgroup => secgroup.id)],
});
