    name: "security",
    description: "created by terraform",
    type: "active_directory",
    server: "192.168.199.10",
    dnsIp: "192.168.199.10",
    domain: "example.com",
    ou: "CN=Computers,DC=example,DC=com",
    user: "joinDomainUser",
    password: "s8cret",
});
