        /// the existing cluster template.
        /// </summary>
        [Input("volumeDriver")]
        public Input<string>? VolumeDriver { get; set; }

        public ClusterTemplateState()
        {
        }
        public static new ClusterTemplateState Empty => new ClusterTemplateState();
    }
